const LINKS=document.querySelectorAll(".solution__link"),SOLUTIONS=document.querySelectorAll(".solution__item");function addFocus(e){let o=e.target.closest(".solution__item");"Tab"!==e.key&&9!==e.keyCode||(SOLUTIONS.forEach((e=>e.classList.remove("active"))),o.classList.add("active"))}function removeFocus(){SOLUTIONS.forEach((e=>e.classList.remove("active")))}LINKS.forEach((e=>{e.addEventListener("keyup",addFocus)})),window.addEventListener("click",removeFocus);